							,MHOBBY
							,MINFO
							,MPHOTO
							,MADMIN
							,DELETEYN
							,INSERTDATE
							,UPDATEDATE
						  )
					VALUES(
							 #{mnum, jdbcType=VARCHAR}
							,#{mname, jdbcType=VARCHAR}
							,#{mid, jdbcType=VARCHAR}
							,#{mpw, jdbcType=VARCHAR}
							,#{mgender, jdbcType=VARCHAR}
							,#{mbirth, jdbcType=VARCHAR}
							,#{mhp, jdbcType=VARCHAR}
							,#{mtel, jdbcType=VARCHAR}
